INSERT INTO `kyrsovaya_v0`.`tag` (`tag_id`, `name`, `description`) VALUES ('1', 'RPG', ' Жанр игр, где основой игрового процесса является отогревание определенной роли');
INSERT INTO `kyrsovaya_v0`.`tag` (`tag_id`, `name`, `description`) VALUES ('2', 'Survival', 'Жанр компьютерных игр, разновидность симуляторов жизни, в которых основной целью игрока является сохранение жизни виртуального персонажа на фоне множества угрожающих ему опасностей');
INSERT INTO `kyrsovaya_v0`.`tag` (`tag_id`, `name`, `description`) VALUES ('3', 'Roguelike', 'Жанр игр, в которых уровни при каждом забеге генерируются случайным образом, а персонаж после поражения теряет значительную часть прогресса');
INSERT INTO `kyrsovaya_v0`.`tag` (`tag_id`, `name`, `description`) VALUES ('4', 'Adventure', 'Жанр компьютерных игр, представляющий собой интерактивную историю с главным героем, управляемым игроком');
INSERT INTO `kyrsovaya_v0`.`tag` (`tag_id`, `name`, `description`) VALUES ('5', 'FPS', 'Жанр компьютерных игр, в которых игровой процесс основывается на сражениях с использованием огнестрельного или любого другого оружия с видом от первого лица таким образом, чтобы игрок воспринимал происходящее глазами протагониста.');
INSERT INTO `kyrsovaya_v0`.`tag` (`tag_id`, `name`, `description`) VALUES ('6', 'Deck builder', 'Жанр игр, в которых игровой процесс основывается на построени \"колоды\" из карт или иных обектов, котрые позволят продвигаться в игре');
INSERT INTO `kyrsovaya_v0`.`tag` (`tag_id`, `name`, `description`) VALUES ('7', 'Action', 'Жанр компьютерных игр, в котором делается упор на эксплуатацию физических возможностей игрока, таких как зрительно-моторная координация и скорость реакции');
INSERT INTO `kyrsovaya_v0`.`tag` (`tag_id`, `name`, `description`) VALUES ('8', 'Strategy', 'Жанр компьютерных игр, в котором игроку для победы необходимо применять стратегическое мышление');
INSERT INTO `kyrsovaya_v0`.`tag` (`tag_id`, `name`, `description`) VALUES ('9', 'Metroidvania', 'Жанр компьютерных игр, отличительной чертой которого является большой связный мир, доступный для исследования, хотя проходы к некоторым его частям перекрыты «дверями», «нехваткой информации» и другими барьерами, которые можно будет преодолеть, подобрав в другом месте мира подходящий инструмент или изучив способность.');
INSERT INTO `kyrsovaya_v0`.`tag` (`tag_id`, `name`, `description`) VALUES ('10', 'Point and click', 'Жанр компьютерных игр, поджанр адвенчур, но с упором на изучение игровых локаций посредством курсора.');
INSERT INTO `kyrsovaya_v0`.`tag` (`tag_id`, `name`, `description`) VALUES ('11', 'Platformer', 'Жанр компьютерных игр, в которых основу игрового процесса составляют прыжки по платформам, лазанье по лестницам, сбор предметов, необходимых для победы над врагами или завершения уровня.');
INSERT INTO `kyrsovaya_v0`.`tag` (`tag_id`, `name`, `description`) VALUES ('12', 'Puzzle', 'Жанр компьютерных игр, целью которых является решение логических задач, требующих от игрока задействования логики');
